import React from 'react'
import { useState, useEffect } from 'react'

function Todos() {
    const [todos, setTodos] = useState([])

      useEffect(() => {
        axios.get('https://localhost:3001/todos')
        .then(res => setTodos(res.data()))
    }, [])
    console.log(todos)


    return (
      <div className="App">
        <h1>ToDo List</h1>
      <ul>
      {todos.map((todo, i) => {
                return(
                       <h3>{todo.description} </h3> 
                );
            })}
      </ul>
      </div>
    );
  }
