import {getTodos} from '../services/getRequests.js'
import { useNavigate, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
//import {getTodos} from './Show.js'

 function Todos() {
  const nav = useNavigate()
  const {id, description} = useParams()
    const [todos, setTodos] = useState([])

      useEffect(() => {
          getTodos()
        .then(res => res.json())
        .then(res => setTodos(res))
      }, [])
    //console.log(todos)

    const deleteTheToDo = () => {
      deleteTheToDo([])
      nav('/')
    }


    return (
      <div className="App">
        <nav className="nav">
        <h1>To_Do List</h1>
        </nav>
      <ul className="ul">
      {todos.map((todo, i) => {
            return(
                <ul >
                    <li key= {i}>
                       <h3> {todo.description}</h3>
                       <button onClick={deleteTheToDo}>DELETE</button> 
                    </li>
                </ul>
                );
            })}
      </ul>
      </div>
    );
  }

  export default Todos;
