import '../App.css'
import { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import {getTodo, deleteToDo, updateToDo} from '../services/getRequests.js'


export default function Show() {
    const nav = useNavigate()
    const {id} = useParams()
    const [todo, setTodo] = useState({})

    useEffect(() => {
        getTodo(id)
        .then(res => setTodo(res.data))
    }, [])
   // console.log(todo)

   const updateTheToDo = () => {
    updateToDo(id)
    nav('/')
    .then(todo => {
        setTodo({todo: todo})
    })
}

    const deleteTheToDo = () => {
        deleteToDo(id)
        nav('/')
      }

    return (
      <div>
        <ul>
         <li>
          <div className={todo.complete ? 'complete' : '' }> {todo.description} </div>
             <h2> ToDo </h2>
            <h3> {todo.description} </h3> <br/>
            <button onClick={() => deleteTheToDo(todo._id)}> Delete</button>
            <button onClick={() => updateTheToDo(todo)}>complete</button>
         </li>
        </ul>
        //  <h3><a href={`/${todo._id}`}>{todo.description}</a></h3>
    </div>
    )
}
