import '../App.css'
import { useNavigate, useParams } from 'react-router-dom'
import {useEffect, useState} from 'react'
import { editTodo, getTodo } from '../services/getRequests.js'

export default function Edit() {
    const [update, setUpdate] = useState({})
    const navigate = useNavigate()
    const {id} = useParams()

    useEffect(() => {
        getTodo(id)
        .then(res => setUpdate(res.update))
    }, [])


    const updateTheTodo = (event) => {
        const editTheTodo = {description:event.target.description.value, complete: event.target.complete.checked}
        updateTodo(editTheTodo)
        navigate ('/')
    }

    return (
        <div className="new">
            <h2 className="edit"> Edit To Do</h2>
           <ul>
           <li>
                    <div className={todo.complete ? 'complete' : '' }> {todo.description} </div>
                    <h3><a href={`/${todo._id}`}>{todo.description}</a></h3>
                    <button onClick={() => updateTheToDo(todo._id)}>Complete</button>             
                    </li>
           </ul>
            {/* <form onSubmit={newTodo}>
            <label htmlFor='description'>Description: </label>
            <input type='text'  name='app' />
            <input type='submit' />
            </form> */}
        </div>
    )
}
