import '../App.css'
import { useNavigate, useParams } from 'react-router-dom'
import {useEffect, useState} from 'react'
import { editTodo, getTodo } from '../services/getRequests.js'

export default function Edit() {
    const [update, setUpdate] = useState({})
    const nav = useNavigate()
    const {id} = useParams()

    useEffect(() => {
        getTodo(id)
        .then(res => setUpdate(res.update))
    }, [])


    const updateTheTodo = e => {
        e.preventDefault()
        const editTheTodo = {description:e.target.description.value, complete: e.target.complete.checked}
        editTodo(editTheTodo)
        nav('/')
    }

    return (
        <div className="new">
            <h2 className="edit"> Edit To Do</h2>
           <ul>
           <li>
                  <form onSubmit={updateTheTodo}>
                      <input type='text' name='description' defaultValue={update.description}/> 
                      <br/>
                      Complete: <input type='checkbox' name='complete' defaultChecked={update.complete} />
                    <h3><a href={`/${todo._id}`}>{todo.description}</a></h3>
                    <button onClick={() => updateTheToDo(todo._id)}>Complete</button> 
                  </form>            
            </li>

           </ul>
            {/* <form onSubmit={newTodo}>
            <label htmlFor='description'>Description: </label>
            <input type='text'  name='app' />
            <input type='submit' />
            </form> */}
        </div>
    )
}
