import { useNavigate, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import {getTodo} from '../components/Show.js'

 function Todos() {
  const nav = useNavigate()
  //returns an object of key/value pairs of URL parameters ...accessing the value of URL parameters
  const {id} = useParams()
    const [todos, setTodos] = useState([])

      useEffect(() => {
          getTodo(id)
        .then(res => res.json())
        .then(res => setTodos(res))
      }, [])
    //console.log(todos)


    return (
      <div className="App">
          <nav className="nav">
            <h1>To_Do List</h1>
          </nav>
        <ul className="ul">
         {todos.map((todo, i) => {
            return(
                <ul >
                    <li key= {i}>
                    <h3><a href={`/${todo._id}`}>{todo.description}</a></h3>

                    </li>
                </ul>
                );
            })}
        </ul>
      </div>
    );
  }

  export default Todos;
