import '../App.css'
import { useState, useEffect } from 'react'
import {getTodos} from '../services/getRequests'
import Create from './Create.js'


 function Todos(props) {
    const [todos, setTodos] = useState([])
    const {todo} = props

      useEffect(() => {
          getTodos()
        .then(res => setTodos(res.data))
      }, [])
    console.log(todos)



    return (
      <div className="App"> 
            <h1 className="nav">TõDø  Łist</h1>
            <h3></h3>

         {todos.map((todo) => {
            return(
               todo.completed = true ? (
                 <div>
                    <strike><h3 className='completed'><a href={`/${todo._id}`}>{todo.description} </a></h3></strike>
                 </div>
               ) : (

                     <div>
                      <h3 className='notCompleted'><a href={`/${todo._id}`} >{todo.description} </a></h3>                   
                    </div>
               )
                );
            })}


            <h3 className="crt"><a href="/create">Add A ToDo</a></h3>
      </div>
    );
  }

  export default Todos;
