import './App.css';
import {getTodos} from '../services/getRequests.js'
import { useState, useEffect } from 'react'

 function Todos() {
    const [todos, setTodos] = useState([])

      useEffect(() => {
          getTodos()
        .then(res => res.json())
        .then(res => setTodos(res))
      }, [])
    //console.log(todos)


    return (
      <div className="App">
        <h1>ToDo List</h1>
      <ul>
      {todos.map((todo, i) => {
            return(
                <ul>
                    <li key= {i}>
                       {todo.description} 
                    </li>
                </ul>
                );
            })}
      </ul>
      </div>
    );
  }

  export default Todos;
