import './App.css';
import { useState, useEffect } from 'react'
import {getTodos} from './services/getRequests.js'
import Todos from './components/Todos.js'
import {BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'

function App() {
  const [todos, setTodos] = useState([])

    useEffect(() => {
      getTodos()
      .then(res => setTodos(res.json()))
  }, [])
  console.log(todos)

 return (
     <div className="app">
         <h1>TODO List</h1>
         <ul className="list"> {todos.map(todo =>
           <li className="li" key={todo.todos} >{todo.todos}</li>

              )}        
         </ul>
     </div>
)

  return (
    <div className="App">
      <nav className="nav">
        <Link to="/" > Todos </Link>
      </nav>
    <Router >
      <Routes>
        <Route path="/" element={<Todos />}/>
      </Routes>
    </Router>
    </div>
  );
}

export default App;
