import { useNavigate, useParams } from 'react-router-dom'
import '../App.css'
import { useState, useEffect } from 'react'
import {getTodos, editToDo} from '../services/getRequests'
//import Create from '../components/Create.js'

 function Todos() {
  const nav = useNavigate()
  //returns an object of key/value pairs of URL parameters ...accessing the value of URL parameters
  const {id} = useParams()
    const [todos, setTodos] = useState([])

      useEffect(() => {
          getTodos(id)
        .then(res => setTodos(res.data))
      }, [])
    console.log(todos)

    const updateTheToDo = () => {
      const editTodo = {description:event.target.app.value, complete: false}
      editToDo(id)
      nav('/')
      .then(todo => {
          setTodos({todos: todos})
      })
    }

    return (
      <div className="App"> 
            <h1 className="nav">TõDø  Łist</h1>
        <ul className="ul">
         {todos.map((todo) => {
            return(

                    <li>
                    <div className={todo.complete ? 'complete' : '' }> {todo.description} </div>
                    <h3><a href={`/${todo._id}`}>{todo.description}</a></h3>
                    <button onClick={() => updateTheToDo(todos._id)}>Complete</button>             
                    </li>

                );
            })}
        </ul>
      </div>
    );
  }

  export default Todos;
