import React from 'react'
import {getTodos} from '../services/getRequests.js'
import { useState, useEffect } from 'react'
import {CreateTask} from '../components/CreateTask.js'

 function Todos() {
    const [todos, setTodos] = useState([])

      useEffect(() => {
          getTodos()
        .then(res => res.json())
        .then(res => setTodos(res))
      }, [])
    //console.log(todos)




    return (
      <div className="App">
        <nav className="nav">
        <h1>To_Do List</h1>
        {/* {task.title}
                <button onClick={() => completeTask(index)}>Complete</button> */}
        </nav>
      <ul className="ul">
      {todos.map((todo, i) => {
            return(
                <ul >
                    <li key= {i}>
                       <h3> {todo.description} <button>DELETE</button> </h3>
                    </li>
                </ul>
                );
            })}
      </ul>
      </div>
    );
  }

  export default Todos;
