import '../App.css'
import { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import {getTodo, deleteToDo} from '../services/getRequests.js'
import Create from './Create.js'


export default function Show() {
    const nav = useNavigate()
    const {id} = useParams()
    const [todo, setTodo] = useState({})

    useEffect(() => {
        getTodo(id)
        .then(res => setTodo(res.data))
    }, [])
   // console.log(todo)

//    const updateTheToDo = () => {
//     editToDo(id)
//     nav('/')
//     .then(todo => {
//         setTodo({todo: todo})
//     })
//   }

    const deleteTheToDo = () => {
        deleteToDo(id)
        nav('/')
      }

    return (
      <div>

          {/* <div className={todo.complete ? 'complete' : '' }> {todo.description} </div> */}
             <h2> ToDo </h2>
            <h3> {todo.description} </h3> <br/>
            Completed: <input type='checkbox' checked={todo.complete} />
            <button onClick={() => {nav(`${id}/edit`)}}></button>
            <button onClick={() => deleteTheToDo(todo._id)}> Delete</button>
            {/* <button onClick={() => updateTheToDo(todo)}>Complete</button> */}

         {/* <h3><a href={`/${todo._id}`}>{todo.description}</a></h3> */}
      </div>
    )
}
