import {BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'

function App() {
  const [todos, setTodos] = useState([])

    useEffect(() => {
      getTodos()
      .then(res => setTodos(res.json()))
  }, [])
  console.log(todos)



  return (
    <div className="App">
      <h1>ToDo List</h1>
    <ul>
      {todos.map((task,index) =>{
        return (
          <li className="td" key={index}> {TodoList, description}

